var _s = datasources['oneshot'];
var result = 0;

function phase0() {
    var datasets = {
        _op: 'load',
        data: {
            columns: [
                ['data1', 30, 20, 50, 40, 60, 50],
                ['data2', 200, 130, 90, 240, 130, 220],
                ['data3', 300, 200, 160, 400, 250, 250]
            ]
        }
    };
    window._phase3 = 1;
    return datasets;
}

function phase1() {
    var datasets = {
        _op: 'data.colors',
        data1: d3.rgb('#ff0000').darker(1),
        data2: d3.rgb('#00ff00').darker(1),
        data3: d3.rgb('#0000ff').darker(1)
    };
    window._phase3 = 3;
    return datasets;
}

function phase2() {
    var datasets = {
        _op: 'data.colors',
        data1: d3.rgb('#ff0000').darker(2),
        data2: d3.rgb('#00ff00').darker(2),
        data3: d3.rgb('#0000ff').darker(2)
    };
    return datasets;
}

if (_.isUndefined(window._phase3))
    window._phase3 = 0;

switch (window._phase3) {
    case 0:
        result = phase0();
        break;
    case 1:
        window._phase3 = 2;
        break;
    case 2:
        result = phase1();
        break;
    case 3:
        result = phase2();
        break;
    default:
        break;
}

return result;
