var _s = datasources['oneshot'];
var result = 0;

function phase0() {
    var datasets = {
        _op: 'load',
        data: {
            columns: [
                ['sample', 30, 200, 100, 400, 150, 250]
            ]
        }
    };
    window._phase6 = 1;
    return datasets;
}

