        _op: 'xgrids',
        xgrids: [
            { value: 1, text: 'Label 1' },
            { value: 4, text: 'Label 4' }
        ]
    };
    window._phase6 = 3;
    return datasets;
}

function phase2() {
    var datasets = {
        _op: 'xgrids',
        xgrids: [
            { value: 2, text: 'Label 2' }
        ]
    };
    window._phase6 = 4;
    return datasets;
}

function phase3() {
    var datasets = {
        _op: 'xgrids.add',
        xgrids: [
            { value: 3, text: 'Label 3', class: 'hoge' }
        ]
    };
    window._phase6 = 5;
    return datasets;
}

function phase4() {
    var datasets = {
        _op: 'xgrids.remove',
        xgrids: [
            { value:2 }
        ]
    };
    window._phase6 = 6;
    return datasets;
}

function phase5() {
    var datasets = {
        _op: 'xgrids.remove',
        xgrids: [
            { class:'hoge' }
        ]
    };
    window._phase6 = 7;
    return datasets;
}

function phase6() {
    var datasets = {
        _op: 'xgrids',
        xgrids: [{value: 1, text:'Label 1'}, {value: 4, text: 'Label 4'}]
    };
    window._phase6 = 8;
    return datasets;
}

function phase7() {
    var datasets = {
        _op: 'xgrids.remove'
    };
    window._phase6 = 9;
    return datasets;
}

if (_.isUndefined(window._phase6))
    window._phase6 = 0;

switch (window._phase6) {
    case 0:
        result = phase0();
        break;
    case 1:
        window._phase6 = 2;
        break;
    case 2:
        result = phase1();
        break;
    case 3:
        result = phase2();
        break;
    case 4:
        result = phase3();
        break;
    case 5:
        result = phase4();
        break;
    case 6:
        result = phase5();
        break;
    case 7:
        result = phase6();
        break;
    case 8:
        result = phase7();
        break;
    default:
        break;
}

return result;
