settings.value:
// Initial data setup
function initialize() {
    _global.duration = 1*60*30;				// display duration form start timestamp (second)
    _global.columns = [
        ['x'],
        ['Temperature'],
        ['TempHi'],
        ['TempLo'],
        ['Humidity']
    ];

    _global.datasets = {
         _op: 'load',
        data: {
            columns: _global.columns
        }
    };

    pushData(_global.datasets.data.columns);

    _global.init = true;
    return _global.datasets;
}

function pushData(col) {
    col[0].push(moment().toDate());
    col[1].push(datasources["openweather"]["current_temp"]);
    col[2].push(datasources["openweather"]["high_temp"]);
    col[3].push(datasources["openweather"]["low_temp"]);
    col[4].push(datasources["openweather"]["humidity"]);
}

function durationFiltering(col, duration) {
    var threshhold = moment().unix() - duration;
	var first = moment(col[0][1]).unix();		// first timestamp data
    if (first < threshhold) {
        _.each(col, function(v) {
               v = v.splice(1, 1);
		});
    }
    pushData(col);
}

if (_.isUndefined(_global.init) || _global.init === false)
    return initialize();

durationFiltering(_global.columns, _global.duration);

return { columns: _global.columns };

